<script>
export default {
    name: "DesktopSidebar",
    props: ['menus', 'menusAlt'],
    data() {
        return {
            route: 'dashboard',
            type: 'success'
        }
    },
    methods: {
        activeClasses(route) {
            return (route === this.$route.path) ? 'bg-asg-800 text-white' : 'text-asg-100 hover:text-white hover:bg-asg-600';
        },
        clicked(label) {
            this.$emit('route-change');
            this.$toast.open({message: 'You clicked ' + label, type: this.type});
        }
    }
}
</script>
